import Login from "../components/Login";
import api from "../utils/api";

class LoginPage extends Component {
    state = {
        userName: '',
        password: ''
    }

    handleOnChange = (event) => {
        const {name, value} = event.target

        this.setState({ 
            [name] : value

        })
    }

    handleOnSubmit = (event) => {
