import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch, Redirect } from "react-router-dom";
import Home from "./pages/Home.js";
import LoginPage from "./pages/LoginPage.js";
import Register from './pages/Register';
import Profile from './pages/Profile';
import Game from './pages/Game';
import MainNav from "./components/MainNav.js";
import api from "./utils/api";

class App extends Component {
  state = {
    email: "",
    isLogin: false
  }

  handleOnSubmit = (loggedIn) => {
     if(loggedIn) {
       api.logout().then(res => {
         console.log(res.data.email, "logged out");
       })
     } else {
       api.login().then(res => {
         console.log(res.data.em)
       })
     }
 }

  componentDidMount() {
    api.getUser().then(res => {
      console.log(res.data.email, "email");
      if(res.data.email.length > 0 ) {
        this.setState({ isLogin : true})
      } 

    })
  }
  render() {
    return (
      <Router>

        {window.location.pathname != "/login" ? <MainNav isLogin={this.state.isLogin} handleOnSubmit={this.handleOnSubmit} email={this.state.email != undefined ? this.state.email : ""}/> : ""}

        <Switch>
          <Route exact path={'/login'}>
            <LoginPage />
          </Route>
          <Route exact path={'/register'}>
            <Register />
          </Route>
          <Route exact path={'/'}>
          <Home />
          </Route>
          <Route exact path={'/profile'}>
            <Profile />
          </Route>
        </Switch>
      <Switch>
        <Route path={'/games/:id'}>
          <Game />
        </Route>
      </Switch>
    </Router>
    )
  }

}

export default App;
