import React, {useState} from "react";
import {
  Container,
  Row,
  Col,
  Carousel,
  CarouselItem,
  CarouselControl,
} from "reactstrap";

const ScreenShots = (props) => {

 props.gameData !=undefined? console.log(props.gameData.screenshots, "Props Gamedata")
: console.log("no found")
 const [activeIndex, setActiveIndex] = useState(0);
  const [animating, setAnimating] = useState(false);

  const next = () => {
    if (animating) return;
    const nextIndex =
      activeIndex === props.screenshots.length - 1 ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  };

  const previous = () => {
    if (animating) return;
    const nextIndex =
      activeIndex === 0 ? props.screenshots.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  };

  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  };

  return (
    <Container className="bg-black">
      <Row>
        <Col sm="12" md={{ size: 10, offset: 1 }}>


        {props.gameData != undefined 
              ? props.gameData.screenshots.map((screenshot) => {
                  return (
                    <image
                      onExiting={() => setAnimating(true)}
                      onExited={() => setAnimating(false)}
                      key={screenshot.src}
                    >
                      <img src={screenshot.url} />
                    </image>
                  );
                })
              : ""}

          {/* <Carousel activeIndex={activeIndex} next={next} previous={previous}>
            {props.gameData != undefined 
              ? props.gameData.screenshots.map((screenshot) => {
                  return (
                    <CarouselItem
                      onExiting={() => setAnimating(true)}
                      onExited={() => setAnimating(false)}
                      key={screenshot.src}
                    >
                      <img src={screenshot.url} />
                    </CarouselItem>
                  );
                })
              : ""}
            <CarouselControl
              direction="prev"
              directionText="Previous"
              onClickHandler={previous}
            />
            <CarouselControl
              direction="next"
              directionText="Next"
              onClickHandler={next}
            />
          </Carousel> */}
        </Col>
      </Row>
    </Container>
  );
};

export default ScreenShots;
